NISTSchemaNCNamePattern2 pattern_2_3 = (NISTSchemaNCNamePattern2)unmarshal("pattern-2-3.xml");
pattern_2_3.setValue(expectedValue);

Status status = validate(pattern_2_3);
if (!status.isPassed()) {
    return status;
}

String testedValue = pattern_2_3.getValue();

if (!expectedValue.equals(testedValue)) {
    String msg = "Wrong value: " + testedValue;
    msg += " Expected: " + expectedValue;
    return Status.failed(msg);
}

return Status.passed("OKAY");

