CodeSet.SupportCode:


final BigInteger[] validValues = {new BigInteger("-72891242177072146369"), new BigInteger("-9999999999999999999999")};
final BigInteger[] invalidValues = {new BigInteger("-72891242177072146368"), new BigInteger("-728912421"), 
                                    new BigInteger("-1"), new BigInteger("0"), new BigInteger("72891242177072146368")};

JAXBElement<BigInteger> elemObj;

Status testInvalid() {
    for (int i = 0; i < invalidValues.length; ++i){

            elemObj.setValue(invalidValues[i]);
            Status status = validate(elemObj);
            if (status.isPassed()) {
                return Status.failed("" + invalidValues[i] + " is unexpectedly valid");
            }

    }
    return Status.passed("OK");
}

Status testValid() {
    for (int i = 0; i < validValues.length; ++i){
        elemObj.setValue(validValues[i]);
        Status status = validate(elemObj);
        if (!status.isPassed()){
            return Status.failed("" + validValues[i] + " is unexpectedly invalid");
        }
    }
    return Status.passed("OKAY");
}
